 export  const isToday = (dateInput:any)=> {
    const inputDate = new Date(dateInput);
    const today = new Date();
    
    return (
        inputDate.getFullYear() === today.getFullYear() &&
        inputDate.getMonth() === today.getMonth() &&
        inputDate.getDate() === today.getDate()
    );
}


